import React, { useState, useEffect } from "react";
import { CityService } from "../../services/city.service";
import {
  FlatList,
  View,
  SafeAreaView,
  StatusBar,
  StyleSheet,
  Text,
  TouchableOpacity,
} from "react-native";
import { useHistory } from "react-router";
import { List } from "react-native-paper";

export const AllCities = () => {
  const history = useHistory();

  const cityService = new CityService();
  const [cities, setCities] = useState([]);

  useEffect(() => {
    cityService.getAllCities().then((el) => {
      setCities(el);
    });
  }, []);

  const [selectedId, setSelectedId] = useState(null);

  const Item = ({ item, onPress, style }) => {
    return (
      <TouchableOpacity onPress={onPress} style={[styles.item, style]}>
        <Text style={styles.title}>{item.name}</Text>
      </TouchableOpacity>
    );
  };

  const navigateToCity = (cityId) => {
    history.push(`/city/${cityId}`);
  };

  const renderItem = ({ item }) => {
    const backgroundColor = item.id === selectedId ? "#6e3b6e" : "white";
    return (
      <Item
        item={item}
        onPress={() => navigateToCity(item.id)}
        style={{ backgroundColor }}
      />
    );
  };

  const sasok = (id) => {
    console.log(id);
  };

  return cities.map((el) => {
    return (
      <View key={el.id}>
        <TouchableOpacity onClick={sasok(el.id)}>
          <Text>&#9733;</Text>
        </TouchableOpacity>
        <Text>{el.name}</Text>
      </View>
    );
  });

  // <SafeAreaView style={styles.container}>
  //   <FlatList
  //     data={cities}
  //     renderItem={renderItem}
  //     keyExtractor={(item) => item.id.toString()}
  //     extraData={selectedId}
  //   />
  // </SafeAreaView>
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight || 0,
  },
  item: {
    padding: 20,
    marginVertical: 8,
    marginHorizontal: 16,
  },
  title: {
    fontSize: 32,
  },
});
