import React, { useState, useEffect } from "react";
import { View, Text } from "react-native";
import { CityService } from "../../services/city.service";
import { WeatherService } from "../../services/weather.service";
import { TemperatureCalculator } from "../../utils/temperatureCalculator";

export const MyCity = () => {
  const cityService = new CityService();
  const weatherService = new WeatherService();
  const tempCalc = new TemperatureCalculator();

  const [myCity, setMyCity] = useState({});

  useEffect(() => {
    cityService.getClientCity().then((el) => {
      console.log(el);
      weatherService.getWeatherByCityName(el).then((el1) => {
        console.log(el1);
        setMyCity(el1);
      });
    });
  }, []);

  return (
    <View>
      <Text style={{ textAlignVertical: "center", textAlign: "center" }}>
        My City
      </Text>

      <Text>City: {myCity.name}</Text>
      <Text>Feels like: {myCity.main?.feels_like}</Text>
      <Text>Temperature: {myCity.main?.temp}</Text>
      <Text>Max Temperature: {myCity.main?.temp_max}</Text>
      <Text>Min Temperature: {myCity.main?.temp_min}</Text>
    </View>
  );
};
