    //   <Text>Temperature: {myCity.main?.temp} &#176;C</Text>
    //   <Text>Max Temperature: {myCity.main?.temp_max} &#176;C</Text>
    //   <Text>Min Temperature: {myCity.main?.temp_min} &#176;C</Text>
    //   <Text>Wind</Text>
    //   <Text>Speed: {myCity.wind?.speed} m/s</Text>
    //   <Text>Degree: {myCity.wind?.deg} &#176;</Text>
